import{stores}from"./stores/index.js";(()=>{if(storesJsonFile){const e=stores.getStoreFromUrl();stores.get(storesJsonFile).then(({ciudades:r})=>{const s=r[e.city],t=r[e.city][0],o=document.querySelector("#stores"),n=[];Object.keys(r[e.city].tiendas).length>1&&(Object.values(s.tiendas).forEach(e=>{n.push(stores.render.store({address:e.direccion,city:s.label,howToGet:e.como_llegar,label:t,link:e.ir,name:e.nombre_tienda,schedule:e.horario_apertura}))}),Promise.all(n).then(e=>{o.innerHTML="",e.forEach(e=>o.insertAdjacentHTML("beforeend",`${e}`))}))})}})();