/*! stores - v1.2.3 */
import{stores}from"./stores/index.js";(()=>{if(storesJsonFile){const e=stores.getStoreFromUrl();stores.get(storesJsonFile).then(({ciudades:r})=>{const t=r[e.city],o=r[e.city][0],s=document.querySelector("#stores"),n=[];Object.keys(r[e.city].tiendas).length>1&&(document.querySelector(".nombre-ciudad").innerHTML=t.label,Object.values(t.tiendas).forEach(e=>{n.push(stores.render.store({address:e.direccion,city:t.label,howToGet:e.como_llegar,label:o,link:e.ir,name:e.nombre_tienda,schedule:e.horario_apertura}))}),Promise.all(n).then(e=>{s.innerHTML="",e.forEach(e=>s.insertAdjacentHTML("beforeend",`${e}`))}))})}})();